void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec4 col = vec4(0.0, 0.0, 0.0, 1.0);
    vec3 scene = texture(iChannel1, uv).rgb;
    vec2 mask = texture(iChannel0, uv).rg;

    vec3 maskColor = mix(ERASEDCOLOR, ERASINGCOLOR, mask.r);
    col.rgb = mix(scene, maskColor, mask.g);

    fragColor = col;
}

int GetNeighbors(vec2 gridCoord)
{
    int num = 0;
    int gridSize = int(GRIDSIZE);
    for(int x = -1; x <= 1; x++)
    {
        for(int y = -1; y <= 1; y++)
        {
            if(x == 0 && y == 0)
                continue;
            num += texelFetch(iChannel0, ivec2(gridCoord) + ivec2(x , y) * gridSize, 0).r > 0.5 ? 1 : 0;
        }
    }


    return num;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 gridCoord = GetGridCoord(fragCoord);
    vec2 gv = gridCoord /iResolution.xy;
    vec4 col = vec4(0.0, 0.0, 0.0, 1.0);
    col.rgb = texelFetch(iChannel0, ivec2(gridCoord), 0).rgb;
    if(iFrame < 10)
    {
        // Initialize pattern
        col.r = texture(iChannel1, gv).r;
    }
    else if(iFrame % int(60.0 * UPDATETIME) == 0)
    {
        bool alive = texelFetch(iChannel0, ivec2(gridCoord), 0).r > 0.5;
        int num = GetNeighbors(gridCoord);
        float val = 0.0;
        if((alive && num == 2) || num == 3)
            val = 1.0;
        else
            val = 0.0;    
        col.r = val;
        col.g = clamp(col.r + col.g, 0.0, 1.0);
    }


    // Adding val by mouse input
    float input_val = step(length(iMouse.xy - fragCoord.xy) , BRUSHSIZE);
    col.r = clamp(col.r + input_val, 0.0, 1.0);

    fragColor = col;
}

#define UPDATETIME 0.1
#define GRIDSIZE 5.0
#define BRUSHSIZE 10.0
#define ERASINGCOLOR vec3(0.2, 0.6, 0.2)
#define ERASEDCOLOR vec3(0.6, 0.2, 0.2)
vec2 GetGridCoord(vec2 fragCoord)
{
    return GRIDSIZE * floor(fragCoord/GRIDSIZE);
}
