// fork of https://www.shadertoy.com/view/4sBfRd Shader Text for beginners by PrzemyslawZaworski

// Fixed offset, changed start to upper left corner, added macros for carriage return, line feed, new line (jt).

//thanks to Fabrice Neyret for algorhitm https://www.shadertoy.com/view/llySRh
//and thanks to otaviogood for font texture
#define C(c) O+= char(U,64+c) ; U.x-=.5

#define LF (U.y++)
#define CR (U.x = (( uv.x - position.x)*64.0/FontSize))
#define NL (CR,LF)

vec4 char(vec2 p, int c) 
{
    if (p.x<.0|| p.x>1. || p.y<0.|| p.y>1.) return vec4(0,0,0,1e5);
	return textureGrad( iChannel0, p/16. + fract( vec2(c, 15-c/16) / 16. ), dFdx(p/16.),dFdy(p/16.) );
}

void mainImage( out vec4 O, vec2 uv )
{
    O = vec4(0.0);
    uv /= iResolution.y;
    float FontSize = 4.;
    vec2 position = vec2(0,1.0 - FontSize / 64.0);
    vec2 U = ( uv - position)*64.0/FontSize;
    C(8);C(5);C(12);C(12);C(15);C(-32);C(23);C(15);C(18);C(12);C(4);C(-31);
    NL;
    C(0); C(8);C(5);C(12);C(12);C(15);C(-32);C(23);C(15);C(18);C(12);C(4);C(-31);
    O = O.xxxx;
}
