void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texture(iChannel0,fragCoord/iResolution.xy);
}

int p(ivec2 c){
    return texture(iChannel0, (vec2(c)+0.5)/iResolution.xy).r<0.5?1:0;
}

bool automata(int rule,ivec2 pc){
    return iFrame ==0?
             pc.x+pc.y == 0:
             pc.y ==0 ?
              (rule>>(p(pc+ivec2(-1,0))<<2|p(pc)<<1|p(pc+ivec2(1,0)))&1)==1:
              p(pc+ivec2(0,-1))==1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord){
    fragColor = automata(30,ivec2(fragCoord-0.5))?vec4(.4,.4,.4,1):vec4(1,1,0,1);
}
