vec2 rotate(vec2 c, float a) {
  return vec2(c.x*cos(a)-c.y*sin(a),c.x*sin(a)+c.y*cos(a));
}
void mainImage( out vec4 c, in vec2 coord )
{
    vec2 coo = coord-(iResolution.xy / vec2(2.0));
    vec2 co = coord-(iResolution.xy / vec2(2.0));
    coord = coord -(iResolution.xy / vec2(2.0));
    coord=rotate(coord,-iTime/2.0-abs(coord.x/2.0)-abs(coord.y/2.0)-abs(abs(coord.x-coord.y)/2.0)+-abs(abs(coord.x+coord.y)/2.0));
    float x = float(coord.x);
    float y = float(coord.y);
    float r = (sqrt(x*x+y*y));
    float a=degrees(atan(y,x))+180.0;
    float R=r+sin(a/11.44)*float((r)/3.5);
    c=vec4(0);
    if (R<100.0)c=vec4(1,0.8,0,1);
    R=r-sin(a/11.44)*float(int(r));
    if (R<66.0)c=vec4(0.5,0.05,0,0);
}
