/////////////////////////////////////////////////////////////////////////
//                                                                     //
//     Fast & Simple Reaction Diffusion Pattern Variation 3            //
//                                                                     //
//             by Benoit Marini - 16/11/2021                           //
//                                                                     //
/////////////////////////////////////////////////////////////////////////
//                                                                     //
// from "Fast & Simple & Dirty Reaction Diffusion Pattern"             //
// Only using mipmaps for convolutions                                 //
// Basic idea here :                                                   //
// https://www.shadertoy.com/view/flGGzR                               //
//                                                                     //
// This shader is a variation of                                       //
// https://www.shadertoy.com/view/slyGR1                               //
//                                                                     //     
/////////////////////////////////////////////////////////////////////////

M
    T(-f*float(2*iFrame),0.);                    // FORM (stored on alpha channel)
    o.rgb =0.5-step(.3,o.a) * cos(o.r*vec3(2,3,4)); // COLOR from green channel (multiplied by Form)
}

M

   step (.01,T(f,0.)-T(f,3.5)) ;    // FORM o = step (.01,T(f ..... 
   o.r = (o.a>.5)? T(f,0.).r : -.5; //COLOR
   if( T(f,4.5).r <.1 ) o.r+=.09;   //COLOR

}



//////////////////////////////////////////////////////////////////////////////////
// FORM (alpha channel) : high pass filter & threshold of Buffer B 
