void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 10.*(fragCoord - .5*iResolution.xy) / iResolution.y;

    float t = iTime;
    float z = sin(sin(uv.x + t) + cos(uv.y + t*1.618)) -
              cos(sin(uv.x*uv.y) + cos(uv.x));
    float c = sqrt(abs(z))*.8;

    fragColor = vec4(c, c, c, 1.0);
}
