/* This method works for explicit ( y=f(x) ) and implicit ( d=f(x,y) ) functions. */
#define ORIGIN                    ( vec2(0) )
#define SCENE_SCALE               ( 1.5 )
#define UNIT                      ( 4.0*SCENE_SCALE / iResolution.y )          // Affects AA and thickness
#define SMAA(x)                   ( smoothstep(UNIT, 0., x) )

float y_axisSDF(vec2 NDC) { return abs(NDC.x); }
float x_axisSDF(vec2 NDC) { return abs(NDC.y); }
float vertical_lineSDF(vec2 NDC, float x0) { return abs(NDC.x-x0); }
float horizontal_lineSDF(vec2 NDC, float y0) { return abs(NDC.y-y0); }
float cellsSDF(vec2 NDC)
{
    NDC = fract(NDC); NDC = fract(min(NDC, 1. - NDC));
    return
        min(vertical_lineSDF(NDC, 0.), horizontal_lineSDF(NDC, 0.));
}
float draw_explicit_func(vec2 NDC, float fx, float dfdx)
{
    float sdf = NDC.y - fx, L2_norm = sqrt(1.0 + dfdx * dfdx);
    return SMAA(abs(sdf) / L2_norm);
    //return SMAA(abs(sdf) / L2_norm - 0.05); // thick

}
float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / sqrt(grad.x*grad.x + grad.y*grad.y)); } // L2-norm
//float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / (abs(grad.x) + abs(grad.y))); }       // L1-norm
//float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / max(abs(grad.x), abs(grad.y))); }     // Linf-norm
//float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / sqrt(grad.x*grad.x + grad.y*grad.y) - 0.05); } // thick
// e.t.c.
// variants with fwidth                                                                       fwidth
//float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / (abs(grad.x) + abs(grad.y))); }
//float draw_implicit_func(vec2 NDC, float fxy, vec2 grad) { return SMAA(abs(fxy) / (fwidth(fxy)*120.)); }

/* https://www.shadertoy.com/view/DdsGDj */
#define MOUSE_OFFSET ( iMouse.z > 0. ? iMouse.xy - iResolution.xy * 0.5 : vec2(0) )
vec2 map_to_centered_ndc(in vec2 SC, in float scale, in vec2 origin, in bool mouse_drag)
{
    vec2 M = MOUSE_OFFSET * (mouse_drag == true ? 1. : 0.);
    return ((2. * (SC - M) - iResolution.xy) / iResolution.y) * scale - origin;
}

// Explicit function definition.
#define M ( 3. )
float explicit(float x) { return cos(M*x); }  // Just redefine this.
float explicit_dydx(float x)
{
    /*Numeric derivative.*/
    const float dx = 0.01;
    return ( explicit(x+dx) - explicit(x-dx) ) / (2.*dx);
    /* Analytic derivative. *
    return -M*sin(M*x);
    /**/
}
//vec2 explicit_tangent(float x) { return vec2(1., explicit_dydx(x)); }
//vec2 explicit_gradient(float x) { return vec2(1., explicit_dydx(x)); }
//vec2 explicit_normal(float x) { vec2 T = explicit_tangent(x); return vec2(-T.y, T.x); }

// Implicit function definition.
float implicit(float x, float y) { return x*x+y*y-1.; } // Just redefine this.
float delf_delx(float x, float y)
{ 
    /* Numeric derivative. */
    const float dx=0.001;
    return ( implicit(x+dx, y)-implicit(x-dx, y) ) / (2.*dx);
    /* Analytic derivative. *
    return 2.*x;
    /**/
}
float delf_dely(float x, float y) 
{ 
    /* Numeric derivative. */
    const float dy=0.001;
    return ( implicit(x, y+dy)-implicit(x, y-dy) ) / (2.*dy);
    /* Analytic derivative. *
    return 2.*y;
    /**/
}
vec2 implicit_gradient(float x, float y) { return vec2(delf_delx(x, y), delf_dely(x, y)); }
//vec2 implicit_normal(float x, float y) { return vec2(delf_delx(x, y), delf_dely(x, y)); }
//vec2 implicit_tangent(float x, float y) { return vec2(delf_dely(x, y), -delf_delx(x, y)); }
//float implicit_dydx(float x, float y) { return -delf_delx(x, y) / delf_dely(x, y); }
//float implicit_deriv(float x, float y) { return -delf_delx(x, y) / delf_dely(x, y); }

void mainImage( out vec4 O, in vec2 SC )
{
    vec2 NDC = map_to_centered_ndc(SC, SCENE_SCALE, ORIGIN, true);

    vec3 color = vec3(1);
         color = mix( color, vec3(0.8), SMAA(cellsSDF(NDC)) );
         color = mix( color, vec3(0.9), SMAA(cellsSDF(NDC*10.)-0.025) );
         color = mix( color, vec3(0), SMAA(x_axisSDF(NDC)) );
         color = mix( color, vec3(0), SMAA(y_axisSDF(NDC)) );

    /* Explicit function y=f(x) */
         color = mix(color, vec3(0,0,1), draw_explicit_func(NDC, explicit(NDC.x), explicit_dydx(NDC.x)));

    /* Implicit function f(x,y) */
         color = mix(color, vec3(1,0,0), draw_implicit_func(NDC, implicit(NDC.x, NDC.y), implicit_gradient(NDC.x, NDC.y)));

    O = vec4(color,1.0);
}
