/*
    "One Cube Challenge" by @XorDev

    My attempt at iapafoto's one cube challenge: shadertoy.com/view/4d33R8


    <200 chars playlist: shadertoy.com/playlist/N3lGDN
*/
void mainImage(out vec4 O, vec2 I)
{
    //Raymarcher loop
    for(
    //Resolution for scaling/centering
    vec3 r = iResolution,
    //Ray direction
    d = (vec3(I+I,0)-r)/r.xxz,
    //Ray origin (approximates vec3(0,0,30))
    p = 30./r,
    //Rotation vector (don't know why "q")
    q;

    //Raymarch 99 times (r.z initializes as 1)
    r.z++<1e2;
    //Cube SDF
    q = p += d*length(min(9.-abs(q),.8))-d)

        //Rotate cube yaw (xz) 
        q.xz *= mat2(cos(iTime + vec4(0,11,33,0))),

        //Shiny coloring from derivatives
        O.rgb = 9./r.x / fwidth(q);
}



//3D rotation, B&W version [206 chars]:
/**
void mainImage(out vec4 O, vec2 I)
{
    for(
    vec3 r = iResolution,
    d = (vec3(I+I,.1)-r)/r.yyz,
    p = 30./r, x=r-r+.58;

    r.z++<2e2;

    p += d*length(min(9.-abs(mix(x*dot(p,x), p, cos(iTime)) + sin(iTime)*cross(p,x)),.8))-d)
    O = r.z+p.zzzz;
}
/**/
