bool ANIMATE = true; //customizable
bool STICK = true;   //customizable
bool SKY = true;     //customizable
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord-iResolution.xy/2.0;
    uv/=min(iResolution.y,iResolution.x)/360.0/3.;
    float x=uv.y;float y=-uv.x; float t = iTime; 
    float N=0.8; float N0=1.0; float D=175.0;
    if (ANIMATE) {
        x=x-x*sin((degrees(atan(x,y))/20.0+t*2.0)/2.0)/3.1416/4.0;
        y=y-y*cos((degrees(atan(y,x))/20.0+t*2.0)/2.0)/3.1416/4.0;
    }
    vec4 white, black, red, darkred;
    if(ANIMATE) {
        white = vec4(1.0-x/1280.0);
        black = vec4(0.05-x/2560.0,0,0,1);
        red = vec4(0.7-x/1280.0,0,0,1);
        darkred = vec4(0.5-x/2560.0,0,0,1);
    } else {
        white = vec4(1);
        black = vec4(0);
        red = vec4(1,0,0,1);
        darkred = vec4(0.7,0,0,1);
    }
    float sky = 0.0; float T=t;
    if(T>=250.0) T=250.0;
    if(ANIMATE&&SKY) sky = (-uv.y+T*10.0-800.0)/1600.0;
    fragColor=vec4(0.0,sky/4.0,sky/2.0,1.0);    
    if(abs(y)<=600.0*N0&&abs(x)<=400.0*N0) {fragColor=red;
        if(ANIMATE&&STICK&&y>593.0) {
            fragColor*=abs(605.0-y)/16.0;
        }
    }
    if (abs((-x-D)/sqrt(3.0)+y)<300.0*N0&&abs(y-(-x-D)/sqrt(3.0))<300.0*N0&&-(-x-D)>0.0) fragColor=white;
    if (abs((-x-D)/sqrt(3.0)+y)<280.0*N0&&abs(y-(-x-D)/sqrt(3.0))<280.0*N0&&-(-x-D)>16.0*N0) fragColor=darkred;
    if (abs((x-D)/sqrt(3.0)+y)<300.0*N0&&abs(y-(x-D)/sqrt(3.0))<300.0*N0&&-(x-D)>0.0) fragColor=white;
    if (abs((x-D)/sqrt(3.0)+y)<280.0*N0&&abs(y-(x-D)/sqrt(3.0))<280.0*N0&&-(x-D)>16.0*N0) fragColor=darkred;
    if ((abs((x+D)/sqrt(3.0)+y)<300.0*N0&&abs((x+D)/sqrt(3.0)+y)>280.0*N0
    ||abs(y-(x+D)/sqrt(3.0))<300.0*N0&&abs(y-(x+D)/sqrt(3.0))>280.0*N0
    ||(x+D)>0.0*N0&&(x+D)<16.0*N0)
    &&abs(y)<250.0*N0&&abs(x)<200.0*N0) fragColor=white;
    if (x*x+y*y<170.0*170.0*N*N) fragColor=black;
    if (x*x+y*y<160.0*160.0*N*N&&x>0.0) fragColor=vec4(black);
    else if(x*x+y*y<160.0*160.0*N*N&&x<=0.0) fragColor=vec4(white);
    if(x*x+(y-80.0*N)*(y-80.0*N)<80.0*80.0*N*N) fragColor=vec4(white);
    if(x*x+(y+80.0*N)*(y+80.0*N)<80.0*80.0*N*N) fragColor=vec4(black);
    if(x*x+(y-80.0*N)*(y-80.0*N)<20.0*20.0*N*N) fragColor=vec4(black);    
    if(x*x+(y+80.0*N)*(y+80.0*N)<20.0*20.0*N*N) fragColor=vec4(white);
    if(abs(y)<15.0*N&&x<-80.0*N&&x>-100.0*N) fragColor=black;
    if((x+35.0)*(x+35.0)+(y-95.0*N)*(y-95.0*N)<5.0*5.0*N*N) fragColor=vec4(black);
    if (abs((-x-34.0)/sqrt(3.0)+y-76.0)<4.0*N0&&abs(y-76.0-(-x-34.0)/sqrt(3.0))<4.0*N0&&-(-x-35.0)>0.0*N0) fragColor=black;
    if(y<-78.0*N&&y>-82.0*N&&x<0.0*N&&x>-40.0*N) fragColor=(white);
    if(y<-72.0*N&&y>-88.0*N&&x<-30.0*N&&x>-35.0*N) fragColor=(white);
    if((x-300.0)*(x-300.0)+(y+450.0)*(y+450.0)<50.0*50.0) fragColor = white;
    if((x-300.0)*(x-300.0)+(y+465.0)*(y+465.0)<43.0*43.0) fragColor = red;
    if(abs(x-300.0)<6.0&&abs(y+492.0)<6.0) fragColor=white;
    if(abs(x-300.0+y+492.0)<9.0&&abs(x-300.0-y-492.0)<9.0) fragColor=white;
    if(ANIMATE&&STICK&&(x<-401.0||x<411.0&&x>401.0)&&(y<598.0&&y>573.0)){
        fragColor= vec4(1.0-abs(583.0-y)/20.0);
        fragColor.x-=0.2*(250.0-T)/250.0;
        fragColor.y-=0.1*(250.0-T)/250.0;
        fragColor.z+=0.1*(250.0-T)/250.0;
        }
    if(ANIMATE&&mod(x,4.0)<=2.0&&mod(y,4.0)<=2.0
    &&(abs(y)<=600.0*N0&&abs(x)<=400.0*N0
    ||STICK&&(x<-401.0||x<411.0&&x>401.0)&&(y<598.0&&y>573.0))) fragColor-=0.1;

}
