void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv-=vec2(0.5);
    float angle = atan(uv.x/uv.y);//uv0,0との角度を割り出す
    float l = length(sqrt(pow(uv.x,2.)+pow(uv.y,2.)));//0.0からの距離
    l+=sin(iTime);

    vec3 col = vec3(angle);
    col =(col/vec3(l));//回転部分とと0,0からの距離との値同士を計算する★/がポイント
    col*=vec3(3.);//波の数
    col = fract(col);//値を0.0~1.0にする


    fragColor = vec4(col,1.0);
}
