float Circle(vec2 uv, vec2 p, float r, float blur)
{
    uv.x *= iResolution.x/iResolution.y;   
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;

}
float Ellips(vec2 uv, vec2 p, float a, float b, float r, float blur)
{
    uv.x *= a/b;   
    float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);
    return c;

}





void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 color = vec3(.0);
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= .5;
    uv.y -= .5;

    float c = Circle(uv, vec2(.0, .1), .4, .01);
    c += Circle(uv, vec2(.3, .4), .1, .01);
    c += Circle(uv, vec2(-.3, .4), .1, .01);
    c -= Circle(uv, vec2(.1, .25), .05, .01);
    c -= Circle(uv, vec2(-.1, .25), .05, .01);

    float leg1 = Ellips(uv, vec2(-.2, -.35), 4., 1., .13, .01);
    float leg2 = Ellips(uv, vec2(.3, -.35), 4., 1., .13, .01);
    float foot1 = Ellips(uv, vec2(-.075, -.45), 1.5, 2., .05, .01);
    float foot2 = Ellips(uv, vec2(.09, -.45), 1.5, 2., .05, .01);  

    float hand1 = Ellips(uv, vec2(1, .0), 4.2, 1., .14, .01);
    float hand2 = Ellips(uv, vec2(-1, .0), 4.2, 1., .14, .01);


    float mouth = Circle(uv, vec2(.0, .1), .25, .01);
    mouth -= Circle(uv, vec2(.0, .13), .25, .01);
    c -= mouth;
    c += leg1;
    c += leg2;
    c += foot1;
    c += foot2;
    c += hand1;
    c += hand2;




    color = vec3(1., 1., .0f)*c;
    fragColor = vec4(color, 1.0);
}
