float Circle(vec2 uv, vec2 p, float r, float blur){
  float d = length(uv-p);
  float c = smoothstep(r,r-blur,d);
  return c;
}
void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  vec2 uv = fragCoord.xy/iResolution.xy;
       uv-=.5;
  uv.x *= iResolution.x/iResolution.y; 
  vec3 col = vec3(0.);
  float c = Circle(uv, vec2(0.,-0.2), 0.3, 0.01);
        c += Circle(uv, vec2(0.,0.2), 0.2, 0.01);
        c += Circle(uv, vec2(0.2,0.39), 0.15, 0.01);
        c += Circle(uv, vec2(-0.2,0.39), 0.15, 0.01);
        c += Circle(uv, vec2(-0.3,0.), 0.12, 0.01);
        c += Circle(uv, vec2(0.3,0.), 0.12, 0.01);
        c += Circle(uv, vec2(-0.29,-0.39), 0.13, 0.01);
        c += Circle(uv, vec2(0.29,-0.39), 0.13, 0.01);
        c -= Circle(uv, vec2(-0.08,0.25), 0.04, 0.01);
        c -= Circle(uv, vec2(0.08,0.25), 0.04, 0.01);
  float mouth = Circle(uv, vec2(0. ,0.2), 0.14, 0.01);
        mouth -= Circle(uv, vec2(0. , 0.25), 0.14, 0.01);
  c-=mouth;        
  col = vec3(0.,1.,0.)*c;

  fragColor = vec4(col, 1.0);

}
