const float PI = 3.14;

const float T = PI;
const float W0 = 3./4.;
const float W1 = 1./4.;

vec2 rotZ(vec2 point, float angle) {
    float c = cos(angle);
    float s = sin(angle);
    vec2 finalPoint = mat2(c, -s, s, c) * point;
    return finalPoint;
}

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = (2. * u - Res) / Res.y;
    O = vec4(0.);
    for(float i = 0.; i < 10.; i++) {
        float a = i * 2. * PI/10.;
        float x = cos(a) * .1;
        float y = sin(a) * .1;
        vec2 Uo = vec2(x, y) + U;
        float t = min(iTime, T);
        vec2 U0 = rotZ(Uo, W0 * t);
        vec2 U1 = rotZ(Uo, W1 * t);
        vec4 l0 = vec4(min(.01/abs(U0.y), 1.));
        vec4 l1 = vec4(min(.01/abs(U1.y), 1.));
        O += (0.5 * l0 + l1) * vec4(sin(iTime) * round(atan(Uo.x) * 5.)/5. + cos(iTime) * round(atan(Uo.y) * 5.)/5. + 1.);
    }
}
