mat2 Rotation(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}

vec3 Translation(vec3 p, vec3 T){
    return p - T;
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V u = Plane(p,normalize(vec3(0.0,0.0,1.0)),vec3(0.0,0.0,-4.0),0);

  vec3 Tp = Translation(p, vec3(0.0, 0.0, 0.0));
  vec3 Tp1 = Translation(p, vec3(0.0, 0.0, 0.0));
  vec3 Tp2 = Translation(p, vec3(0.0, 0.0, 0.0));
  Tp.xy = Tp.xy * Rotation(0.8*iTime);
  //Tp.yz = Tp.yz * Rotation(0.8*iTime);
  //Tp1.xy = Tp1.xy * Rotation(0.8*iTime);
  Tp1.yx = Tp1.yx * Rotation(0.8*iTime);
  Tp2.zx = Tp2.zx * Rotation(0.8*iTime);

  u=Union(u,Dice(Tp1,vec3( 0.0, 0.0, 4.0),4.0,1));
  u=Union(u,Dice(Tp2,vec3( -10.0, 0.0, 4.0),4.0,2));
  u=Union(u,Dice(p,vec3( 10.0, 0.0, 4.0),4.0,3));
  u=Union(u,Union(Cube(p,vec3( 10.0, 10.0, 4.0),3.0,4),Sphere(Tp,vec3( 10.0, 10.0, 9.0),2.0,5)));

  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.65,0.75,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}


// Shading and lighting ---------------------------------------------------------------------------

T Texture0(in vec3 p)
{
  return T(vec3(0.20,0.2,0.2),0.25);   
}

T Texture1(in vec3 p)
{
  return T(vec3(0.82,0.81,0.90),0.25);   
}

T Texture2(in vec3 p)
{
  return T(vec3(0.82,0.91,0.80),0.0);   
}

T Texture3(in vec3 p)
{
  return T(vec3(0.82,0.91,0.90),0.0);   
}

T noise(vec3 p)
{
	float v = 0.0;
	float a = 0.65;
	v += a * Noise(p);
    vec3 c;
    c.r=v;// 
    c.g=v;// On applique le résultat aux couleurs 
    c.b=v;//
	return T(c,0.25);
}

float turb(vec3 p) //ou T pour texture en init
{
	float v = 0.0;
	float a = 0.75;
	vec3 shift = vec3(100);
	for (int i = 0; i <8; ++i) {
		v += a * Noise(p);
		p = p * 2.0 + shift;
		a *= 0.5;
	}
    vec3 c;
    c.r=v;//
    c.g=v;// On applique le résultat aux couleurs 
    c.b=v;//
	return v; //ou return T(c,0.25);
}
//La fonction Turbulence en mode texture correspond à une texture de marbre sans veines.

T damier(vec3 p, vec3 a, vec3 b, float r)//a : première couleur, b : deuxième couleur
{
    int x = int(p.x);
    int y = int(p.y);
    int z = int(p.z);

    if((x+y+z)% 2 == 0) //1 fois sur deux la couleur de la case devient blanche
    {
        return T(a,r);
    }
    else{return T(b,r);}// ou elle devient noire
}

T bois(vec3 p, vec3 a, vec3 b, float r)
{
    float d = length(p.yx + Noise(p));//distance avec z auquel on ajoute un Noise pour la déformation
    float v = 0.5*(cos(d*10.0)+1.0); // création des cercles 
    vec3 c = a*v+b*(1.0-v);//couleur prend la valeur de a et b (couleur au choix) et appliqué à v (cercles créés)
    return T(c,r);

}



T marbre(vec3 p, float r)
{
    p.x = p.x + turb(p);
    float t = ((1.0+(cos(p.x*8.0)))/2.0); //Marbre couleur blanc avec ajout de tan pour des lignes 
    //float t = cos((p.x*10.0)/1.5); // Marbre de couleur noir et ajout plusieurs lignes grâce a cosinus
    vec3 c;
    c.r=t;//
    c.g=t;// On applique le résultat aux couleurs 
    c.b=t;//
    return T(c,r);
}



// Compute texture 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= object(p);
    if (vp.i==0){

        return noise(p);
    }
    else if (vp.i==1)
    {
        return bois(p,vec3(0.533,0.2588,0.113),vec3(0.65,0.40,0.15),0.1);
    }
    else if (vp.i==2)
    {

        return damier(p,vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),0.45);
    }
    else if (vp.i==3)
    {
        return marbre(p,0.25);
    }
    else if (vp.i==4)
    {
        return damier(p,vec3(0.0,0.0,0.0),vec3(1.0,1.0,1.0),0.45);

    }
    else if (vp.i==5)
    {
        return marbre(p,0.75);
    }
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p,l,100.0,h,s);
    if (!h)
    {
     return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    T tex=Color(p,n);

    // Ambient color
    vec3 ambient = 0.5*tex.c;

    // Shadow computation
    float s = Shadow(p+0.01*n,n,l);

// Phong diffuse
    vec3 diffuse = 0.5  * clamp(dot(n, l),0.0,1.0) * tex.c;

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = tex.s * pow(clamp(dot(r,l),0.0,1.0),28.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + s * (diffuse + specular);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

// Texturing and noise 

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    // f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Compute the distance to the Voronoi boundary
// x : Point
// Return (closest distance, second closest, cell id)
vec3 Voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + Hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

// Camera -----------------------------------------------------------------------

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3(35.0,0.0,15.0);
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
