float ray1(vec3 rayO, vec3 rayD){

    float a = rayD.x;
    float b = rayD.y;
    float c = rayD.z;

    float x0 = rayO.x;
    float y0 = rayO.y;
    float z0 = rayO.z;


    float alpha = 0.1 * ((a * a * c) + (b * b * c));
    float beta = 0.1 * ((a * a * z0) + (2. * x0 * a * c) + (b * b * z0) + (2. * y0 * b * c));
    float gamma = 0.1 * ((2. * x0 * z0 * a) + (x0 * x0 * c) + (2. * y0 * z0 * b) + (y0 * y0 *c)) + (0.6  * c);
    float rho = 0.1 * ((z0 * x0 * x0) + (z0 * y0 * y0)) + (0.6 * z0) + 1.;

    float P = -beta / (3. * alpha);
    float Q = (P * P * P) + (((beta * gamma) - (3. * alpha * rho)) / (6. * (alpha * alpha)));
    float R = gamma / (3. * alpha);

    float M = (Q * Q) + pow(pow(R - (P * P), 3.), .5);

    float t = pow(Q + M, .333) + pow(Q - M, .333) + P;

    return t;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec4 col = vec4(0.);


    float t0 = ray1(vec3(0.), vec3(1.1));

    //so apparently t0 is neither greater than equal to or 
    //lesser than 0., but it IS a number apparently because
    //isnan(t0) returns false and isinf(t0) returns false also

    if(isnan(t0)){
        col = vec4(.5);
    }
    if(t0 <= 0.){
        col = vec4(.5);
    }
    if(t0 >= 0.){
        col = vec4(.5);
    }
    if(isinf(t0)){
        col = vec4(.5);
    }

    fragColor = col;
}
