float line(in vec2 p, in vec2 a, in vec2 b) {
	vec2 ba = b - a;
	vec2 pa = p - a;
	float h = clamp(dot(pa, ba) / dot(ba, ba), 0., 1.);
	return length(pa - h * ba) + p.y*0.01;
}

float _hlp(float x, float a){
    return min(1.,max(0.,abs(mod(x+a,1.)*6.-2.)-1.));
}

float rand(float co) { return fract(sin(co*(91.3458)) * 47453.5453); }
vec2 rand2(float x) { return vec2(rand(x), rand(x+14.1543346));}
vec3 rand3(float x) { return vec3(rand(x+.153151), rand(x+10.1543346),rand(x+5.32185108));}

vec3 hue(float h){
    return vec3(
        _hlp(h,0.),
        _hlp(h,0.33333333333),
        _hlp(h,0.66666666667)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy-.5)*(iResolution.xy/iResolution.yy);
    vec2 dir= vec2(.1,.2);
    vec3 col = vec3(0.);
    vec3 lp;
    float len;

    for(float i=0.; i<100.; i++){
        lp=rand3(i);
        lp.xy-=.5;
        lp.y-=.1;
        lp.x*=2.;
        len=rand(i)*4.;
        lp.x+=mod(sqrt(lp.z)*(iTime+100.)*.1,5.)-2.5;
        col+=smoothstep(lp.z*.02,0., line(uv, lp.xy, lp.xy+dir*len));
        col+=.5*hue(rand(i))*smoothstep(lp.z*.1,0., line(uv, lp.xy, lp.xy+dir*len))-uv.y*0.01;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
