#define c1 vec3(1.,0.5,1.5)

vec2 triangle_wave(vec2 a,float scale){
    return abs(fract((a+c1.xy)*scale)-.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec3 col = vec3(0.);
    float t1 = 8.;
    vec2 uv = (fragCoord)/iResolution.y/t1/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/t1/8.0;
    float scale = c1.z;
    float offset = 0.;
    float offset1 = iTime/1000.;
    for(int i=0;i<6;i++)
    {
        vec2 t2 = vec2(0.);
        vec2 t3 = vec2(0.);
        for(int k = 0; k < 2; k++){
            uv += 1.5+(t2.yx);
            vec2 div = scale-t3*t2;
            t2 = triangle_wave(uv.yx-.5,scale);
            t3 = triangle_wave(uv,scale);
            uv.yx = (t2+t3+div)/div;

        }
        //if(i>1) uv += iTime+col.x;
        col.x = -max(((uv.y-uv.x)),-col.x);
        //col = (col+vec3(col.x))/2.;
        col = (col.yzx+col);
        col /= 2.;
    }
    fragColor = vec4(vec3(-col*3.),1.0);
}
