void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float move = ceil(iTime);
    float tile = 10.0;
    float res = iResolution.x/iResolution.y;
    vec2 mUV = round(vec2(((uv.x*res+0.01)+move/45.0), uv.y)*tile)/tile;


    vec2 p  = -1.0 + 2.0* vec2(uv.x-move, uv.y);
    p.x *= iResolution.x/iResolution.y;

    vec3 col = fract(vec3(p*tile, 0.0))-0.5;

    col = vec3(smoothstep(length(col)-0.02, length(col), 0.60));
    vec3 tex = texture(iChannel0,mUV).xyz;
    col *= tex;

    // Output to screen
    fragColor = vec4(col,1.0);
}
