//#define Show_All_Cell_Character
//#define SHOW_A_Character_FullScreen
//#define Translate_Character // Offset or translate

vec2 hash22(vec2 co){
    float x = fract(sin(dot(co.xy ,vec2(122.9898,783.233))) * 43758.5453);
    float y = fract(sin(dot(co.xy ,vec2(457.6537,537.2793))) * 37573.5913);
    return vec2(x,y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // vec2 uv = fragCoord / iResolution.y;

    // use iResolution.xy to show the grid cell correctly
    vec2 uv = fragCoord / iResolution.xy;

    #if defined Translate_Character
    vec2 wordPos = vec2(0.5, 0.5);
    uv -= wordPos;// offset or translate, also the word position
    #endif

    #if !defined(SHOW_A_Character_FullScreen)
    uv *= 64.; // a scale factor: make a grid of 64 x 64.
    float s = 8.; // another scale factor: the font size. When s = 64, the letter 'A' will just occupy the full screen. 
    uv /= s; // the grid becomes 8 * 8.
    #endif
    int i = 65; // ASCII code for letter 'A'.
    vec2 index = floor(uv); // the grid cell index of current pixel.
    vec2 r = hash22(index);
    fragColor = vec4(r, 0., 0.); // the grid cell background color.

    // only show the letter in grid cell with index 0.
    #if !defined(Show_All_Cell_Character)
    if (uv.x < 0. || uv.x > 1. || uv.y < 0. || uv.y > 1.) {
        vec4(0,0,0,1e5);
        return;
    }
    #endif


    fragColor += textureGrad(iChannel0, (fract(uv) + vec2(i%16,15-i/16))/16., dFdx(uv/16.),dFdy(uv/16.)).x; 
}
