const float scrollSpeed = .02;
const float distortionSpeed = 0.001;
const float distortion = .25;
const float waveSpeed = .01;
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

   uv+= texture(iChannel1, uv * distortion + vec2(iTime * distortionSpeed, 0.0)).rr * .03;
  //  vec3 col = texture(iChannel0, uv).rgb;


     uv.x *= -1.;
     uv.y *= .5;

     uv.y += iTime * scrollSpeed;


    vec3 col = texture(iChannel0,uv).rgb;

     col.z = col.x;
    col.y = col.x /1.9;
    col.x *= .25;

    vec2 addUV = uv - vec2(0.0, iTime*waveSpeed) + vec2(0.0, texture(iChannel1,uv).r * 0.1);
    float add = texture(iChannel2,addUV).r;
    add = smoothstep(0.65,0.9, add);
    col+= add;




    // Output to screen
    fragColor = vec4(col,1.0);
}
