float horizon = -0.2;//[-0.2,0.2]
float vertical = -0.2;//[-0.2,0.2]

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float ratio = iResolution.x/iResolution.y;
    vec2 offset = horizon * vec2(0.1,0.) + vertical*ratio * vec2(0.,0.1);
    vec4 cr = texture(iChannel0, uv + offset);
    vec4 cga = texture(iChannel0, uv);
    vec4 cb = texture(iChannel0, uv - offset);
    fragColor = vec4(cr.r, cga.g, cb.b, cga.a);
}
