void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 coord = fragCoord/iResolution.xy;

    // Time varying pixel color


    float color = 0.0;

    color += sin(coord.x * 50.0 + cos(iTime + coord.y * 10.0 + sin(coord.x * 50.0 + iTime * 2.0))) * 2.0;
    color += cos(coord.x * 20.0 + sin(iTime + coord.y * 10.0 + cos(coord.x * 50.0 + iTime * 2.0))) * 2.0;
    color += sin(coord.x * 30.0 + cos(iTime + coord.y * 10.0 + sin(coord.x * 50.0 + iTime * 2.0))) * 2.0;
    color += cos(coord.x * 10.0 + sin(iTime + coord.y * 10.0 + cos(coord.x * 50.0 + iTime * 2.0))) * 2.0;



    // Output to screen
    fragColor = vec4(vec3(color + coord.y, color + coord.x, color), 1.0);
}
