bool dentroCuadrado(vec2 centro, float ancho, vec2 fragCoord)
{
    vec2 a = abs( fragCoord - centro );
    return max( a.x, a.y )  < ancho / 2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 modv = mod(fragCoord - iTime * 50., 100.);
    bool dentro = dentroCuadrado(vec2(0), 90., modv);

    if (dentro)
        fragColor = vec4(vec3(0.75, 0.0, 0.75), 1.0);
    else
        fragColor = vec4(vec3(1.0, 1.0, 0.0), 1.0);

}
