#define R iResolution.xy

vec2 triangleGrid(vec2 uv, float zoom)
{  
    uv *= mat2(1., 0., 0.5, 0.866) * zoom; 
    vec2 f = fract(uv);
    return floor(uv) * 2. - step(f.x, f.y);
}

vec2 getCenterByIndex(vec2 index, float zoom)
{
    return index * mat2(1., 0., -0.57735, 1.1547)/(zoom * 4.)
                 * vec2(R.y/R.x, 1.) *2. + 0.5;
}

void mainImage(out vec4 O, in vec2 fragCoord)
{
    // Aspect correct screen coordinates.
    vec2 uv = (fragCoord - R * 0.5)/R.y;
    float zoom = mix(10., 80., iMouse.x/R.x);

    O = texture(iChannel0, getCenterByIndex(triangleGrid(uv, zoom), zoom));
}
