#define COUNT 6.
#define COL1 vec3(188, 188, 188)/255.
#define COL2 vec3(0, 5, 110)/255.

#define rand1(p) fract(sin(p* 78.233)* 43758.5453) 

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 uv = (fragCoord - iResolution.y*.87)/iResolution.y;        

    float sum = 0.;  
    float sumG = 0.;

    for(float i=0.; i<COUNT; i++){
        float dir = mod(i, 2.)*2.-1.;        
        float a = iTime * 0.4+ i + fract(sin(i* 78.233)* 43758.5453) * 3.1415;
        float l = length(uv + dir* vec2( sin(a)/2., cos(a*.5)/4.) );

        sum+=.001/l;
        sumG+=.0175/l;
    }        

    sumG-=0.35;

    float fd = smoothstep(1., .5,  sum);

    vec3 col = mix(COL1, COL2, clamp(fd + sumG*3.95, 0., 0.5));

    fragColor = vec4(col,1.0);
}
