vec3 color1 =vec3(255.0, 220.0, 156.0)/255.0; // orange lighting
vec3 bk_color1 =vec3(46, 42, 39)/255.0; //dark brown


// the following helper function is adapted from https://www.shadertoy.com/view/Md23DV
// returns the 1.0 inside the disk area and 0.0 outside the disk area
// and has a outer glow
float disk(vec2 r, vec2 center, float radius, float glow) {
	float distanceFromCenter = length(r-center);
	float outsideOfDisk = smoothstep( radius-glow, radius, distanceFromCenter);
	float insideOfDisk = 1.0 - outsideOfDisk;
	return insideOfDisk;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //relocate center
    vec2 r = 2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y; 
    //relocate mouse to center
    vec2 mouse = 2.0*vec2(iMouse.xy - 0.5*iResolution.xy)/iResolution.y; 

    vec3 col = bk_color1; //base dark brown

    //calculate lights
    vec2 uv = fragCoord/iResolution.xy; // Normalized pixel coordinates (from 0 to 1)
    float x = (iTime/2.0)+uv.x;
    float flicker1 = ((0.3)*sin(5.0*x)+(0.5)*sin(2.0*x)+0.2*sin(0.2*x)+1.0)/2.0;
    float flicker2 = (0.3*sin(0.2*x)+0.2*sin(7.0*x)+0.3*sin(-7.0*x)+1.0)/4.0;
    float flicker3 = (0.2*cos(4.0*x)+(0.5)*cos(0.3*x)+0.2*cos(9.0*x)+1.0)/2.0;
    float flicker4 = (0.03*cos(5.0*x)+(0.75)*cos(2.0*x)+0.09*cos(0.2*x)+1.0)/3.0;

    col += disk(r, mouse, flicker1, flicker1)*color1;
    col += disk(r, vec2(-mouse.x, -mouse.y), flicker2, flicker2)*color1;
    col += disk(r, vec2(2.0*mouse.x, -1.5*mouse.y), flicker3, flicker3)*color1;
    col += disk(r, vec2(-1.7*mouse.x, 0.5*mouse.y), flicker4, flicker4)*color1;

    // Output to screen
    fragColor = vec4(col,1.0);
}
