void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float r = 0.;
    float g = 0.;
    float b = 0.;

    if (    bool(int(fragCoord.x+iTime*10.)&16)
         && bool(int(fragCoord.y)&16))
    {
        r = 1.;
    }

    if (    bool(int(fragCoord.x+fragCoord.y)&32)
         && bool(int(fragCoord.y-fragCoord.x)&32))
    {
        g = 1.;
    }
    if (    bool(int(fragCoord.x)&64)
         && bool(int(fragCoord.y+iTime*15.)&64))
    {
        b = 1.;
    }

    vec3 col = vec3(r,g,b);

    // Output to screen
    fragColor = vec4(col,1.0);
}
