vec3 rgb(float r, float g, float b) { // from: http://jonobr1.com/
	return vec3(smoothstep(.9, 0.1, r / 255.0), smoothstep(0.1, .9, g / 255.0), smoothstep(.9, 0.1, b / 255.0));
}

vec4 circle(vec2 uv, vec2 pos, float rad, vec3 color) {
	float d = length(pos - uv) - rad;
	float t = clamp(d, 0.0, 1.0);
	return vec4(color, 1.0 - t);
}

float random(vec2 co) {
  return fract(sin(dot(co.xy, vec2(1, 10))) * .09);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

	vec2 uv = fragCoord.xy;
	vec2 center = iResolution.xy * random(uv / 11.33) * cos(iTime * 0.7) * 0.11 + iResolution.xy * 0.4587;
	float radius = pow(37.3 * sin(iTime * 6.717) * iResolution.y, 1.111);

  // Background layer
	vec4 layer1 = vec4(rgb(210.0, 222.0, 228.0) * (1.0 - sin(iTime * 0.7)), 1.0);

	// Circle
	vec3 red = rgb(225.0 * cos(iTime * 0.7), 95.0, 60.0);
	vec4 layer2 = circle(uv, center, radius, red);

  // small circle
  vec3 green = rgb(0.0, 255.0 * sin(iTime * 0.7), 0.0);
  vec4 layer3 = circle(uv, center, radius * 0.75, green);

	// Blend the two layers
  fragColor = mix(layer1, layer2, layer2.a);

  // Add a small circle in the center
  fragColor -= layer3;

    // Add a small circle in the center
  fragColor += circle(uv, center, radius * 0.614, green);

  // Add a small circle in the center
  fragColor -= circle(uv, center, radius * 0.425, green);

  // Add a small circle in the center
  fragColor += circle(uv, center, radius * 0.257, red);

  // Add a small circle in the center
  fragColor -= circle(uv, center, radius * 0.125, red);

  // Add a small circle in the center
  fragColor += circle(uv, center, radius * 0.0625, green);

  // Add a small circle in the center
  fragColor -= circle(uv, center, radius * 0.03125, green);

  // Add a small circle in the center
  fragColor += circle(uv, center, radius * 0.015625, red);

  // Add a small circle in the center
  fragColor -= circle(uv, center, radius * 0.0078125, red);

  // Add a small circle in the center
  fragColor += circle(uv, center, radius * 0.00390625, red);

  fragColor = normalize(fragColor);

}
