float square(vec2 position) {
    vec2 d = abs(fract(position) - 0.5);
    return min(d.x, d.y);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 position = (2.0 * fragCoord - iResolution.xy) / iResolution.y;
    float scale = 1.0;
    float d = square(position / scale) * scale;
    // same colorization that Inigo Quilez uses in his shaders
    vec3 color = vec3(1.0) - sign(d) * vec3(0.1, 0.4, 0.7); // base color
    color *= 1.0 - exp(-4.0 * abs(d)); // gradient
	color *= 0.8 + 0.2 * cos(120.0 * d); // ripples
	color = mix(color, vec3(1.0), 1.0 - smoothstep(0.0, 3.0, (abs(d) - 0.002) * iResolution.y));
    if (iMouse.z > 0.0) {
        vec2 mousePosition = (2.0 * iMouse.xy - iResolution.xy) / iResolution.y;
        float d = square(mousePosition / scale) * scale;
        color = mix(color, vec3(1.0, 1.0, 0.0), 1.0 - smoothstep(0.0, 3.0, abs(length(position - mousePosition) - abs(d)) * iResolution.y - 1.0));
    }
    fragColor = vec4(color, 1.0);
}
