float frequency = 4.0;
float thickness = 0.1;
vec3 bgColor = vec3(0.0,0.5,0.6);
vec3 rippleColor = vec3(0.0,0.8,0.9);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv = uv * 2.0 - 1.0;
    uv.x*=iResolution.x/iResolution.y;
    float d = length(uv);
    d-=iTime*0.1;
    d = fract(d*frequency);
    d = abs(d-0.5)*2.0;
    d*= clamp(1.6 - length(uv),0.0,1.0);
    vec3 col = mix(bgColor,rippleColor,d);
    // Output to screen
    fragColor = vec4(col,1.0);
}
