float hash11(float p)
{
    p = fract(p * .1031);
    p *= p + 33.33;
    p *= p + p;
    return fract(p);
}

void mainImage( out vec4 O, in vec2 u ) {
    vec2 Res = iResolution.xy, 
        U = u / Res.xy;
    float z = hash11(400.) * 400.;
    vec2 U1 = round(U * z); 
    vec2 C = U1 * 1./z + sin(iTime) * 2. * hash11(U1.x)/z + cos(iTime) * 2. * hash11(U1.y)/z;
    O = 0.7 * texture(iChannel0, C) + 0.3 * texture(iChannel0, U);
}
