#define freq(f) texture(iChannel0, vec2(f, 0.25)).x * 0.8


float avgFreq(float start, float end, float step) {
    float div = 0.0;
    float total = 0.0;
    for (float pos = start; pos < end; pos += step) {
        div += 1.0;
        total += freq(pos);
    }
    return total / div;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 icol = vec3(2,2,0);

    float bassFreq = pow(avgFreq(0.0, 0.1, 0.01), 0.85);
    float medFreq = pow(avgFreq(0.1, 0.6, 0.01), 0.85);
    float topFreq = pow(avgFreq(0.6, 1.0, 0.01), 0.85);


    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(uv.xxy+icol);

    float xperiod = 5.;
    float amplitude = 5.;
    float stime = iTime*.1+20.;

    mat2 tr = mat2(sin(uv.y*stime), sin(iTime*.5 + uv.y), 
               -sin(uv.x+stime), -cos(uv.x*stime)
    );

    uv *= tr;

    col += sin(bassFreq*uv.x*sin(uv.x*bassFreq*15.) + amplitude*cos(stime + uv.y*amplitude)) * .9;

    col -= vec3(bassFreq*uv.x+.4, medFreq*uv.y+.4, topFreq*uv.x);
    //col = tan(iTime);
    // Output to screen
    fragColor = vec4(col,1.0);
}
