void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 UV = fragCoord.xy;

	float width = 5.0;
    int groupThread = 1;
	float xp = floor(UV.x/float(groupThread)/width);
	float yp = floor(UV.y/float(groupThread)/width);

    //--- warp
    int overThread = 2;
    int underThread = 1;
    bool warp = mod(abs(xp-yp), float(overThread+underThread))>=float(underThread);
    //fragColor = vec4(warp); return;

	float m = fract( (warp?UV.x:UV.y) /width);
          m = m>0.5 ? 1.-m : m;
    //fragColor = vec4(m); return;

	float settCount = 30.0;
    settCount /= float(groupThread);
	float sett = fract( (warp?xp:yp) /settCount);
          sett = sett>0.5 ? 1.-sett : sett;
    //fragColor = vec4(sett); return;


	vec4 col = 	vec4(0.9,0.1,0.1,1.0);
    col = sett<.1 ? vec4(0.0,.5,0.0,1.0) :
          sett<.2 ? vec4(1.0,1.0,0.0,1.0) : col;


	vec4 bgCol = vec4(0.0);
	//vec4 sideCol = vec4(0.1,0.1,1.0,1.0);
	//fragColor = m<.1 ? bgCol : m<.15 ? sideCol : col;
	fragColor = m<.2 ? bgCol : col; // TODO: maybe you need change threshold number a bit to avoid moire or no bgCol

}
