float fun(float x)
{
    float y = sin(x);
    //float y = cos(x + iTime * 2.0);
    //float y = (iTime + floor(x-iTime))/2.0 - 5.0;

    return y;
}


float getDisInY(vec2 p)
{
    return abs(fun(p.x) - p.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

     //scale
     float scale = 0.1;
     float lineWidth = 0.06;

    float dis = getDisInY(uv / scale);
    vec3 col = (dis < lineWidth) ? vec3(0.0) : vec3(0.65,0.85,1.0);

    fragColor = vec4(col, 1.0);
}
