float fr(float x , float w){
    return x - w*floor(x/w);
}

vec3 led(vec3 col,vec2 uv,vec2 res,float dotsize){
    vec3 w = vec3(0);
    vec3 o = w;

    float wy = 1.8;

    vec2 pixelUV = vec2(fr(uv.x *res.x,1.0),fr(uv.y *res.y,wy));

    vec2 center = pixelUV-vec2(0.5,wy/2.0);

    float l = length(center);
    float r = dotsize;

    vec2 floorUV = uv-pixelUV/res;
    col = texture(iChannel1 , floorUV).xyz;


    float edge = 0.01;
    if(l<r)o =col;

    uv -= vec2(
            1.0/(res.x * 2.0),
            1.0/(res.y * 2.0/wy)
          );
    pixelUV = vec2(fr(uv.x *res.x,1.0),fr(uv.y *res.y,wy));
    floorUV = uv-pixelUV/res;
    col = texture(iChannel1 , floorUV).xyz;

    center = pixelUV-vec2(0.5,wy/2.0);
    l = length(center);
    if(l<r)o =col;
    o *= 1.5;
    return o;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = texture(iChannel0 , uv).xyz;

    col = led(col,uv,(iResolution.xy/10.0),0.37);

    fragColor = vec4(col,1.0);
}
