    float Circle(vec2 uv, vec2 position, float r, float blur) 
    {
        float d = length(uv - position);
        float c = smoothstep(r, r-blur, d);
        return c;
    }




void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord/iResolution.xy; // координаты от 0 до 1  


    uv -= .5;  // центр круга теперь в центре экрана; координаты от -0.5 до 0.5
    uv.x *= (iResolution.x / iResolution.y); 
    // голова
    float c = Circle(uv, vec2(.0, .10), .3, .005);
    //пятачок
    c -= Circle(uv, vec2(.0, .1), .07, .005);
    //ноги
    c += Circle(uv, vec2(.1, -.2), .07, .005);
    c += Circle(uv, vec2(-.1, -.2), .07, .005);
    //руки
    c += Circle(uv, vec2(.3, .03), .07, .005);
    c += Circle(uv, vec2(-.3, .03), .07, .005);
    //уши
    c += Circle(uv, vec2(.1, .4), .05, .005);
    c += Circle(uv, vec2(-.1, .4), .05, .005);

    // глаза
    uv.y *= (iResolution.y / iResolution.x);
    c -= Circle(uv, vec2(.1, .1), .06, .005);    
    c -= Circle(uv, vec2(-.1, .1), .06, .005);       
    c += Circle(uv, vec2(.1, .1), .055, .005);       
    c += Circle(uv, vec2(-.1, .1), .055, .005); 
    c -= Circle(uv, vec2(.1, .1), .02, .005);    
    c -= Circle(uv, vec2(-.1, .1), .02, .005); 

    uv.y *= (iResolution.x / iResolution.y);
    c -= Circle(uv, vec2(.0, .0), .02, .005);
    //пятачок
    uv.y *= (iResolution.y / iResolution.x);
    c += Circle(uv, vec2(.03, .06), .01, .005);       
    c += Circle(uv, vec2(-.03, .06), .01, .005);




      fragColor = vec4(vec3(c), 1.0);



     // fragColor = vec4(col,1.0);
}
