/*

I added a hack to get more action.
In Buffer A when a cell dies there is now
a 0.002 percent chance that it will be reborn as a glowing orb.
All the other things (fire flies, clowns, and brass knuckels)
like to crowd around and sometimes hang out or something.

I mean that is, at least to me, an apt description of what's
been happening so far.

*/

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 0 (from to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 tex = texture(iChannel0,uv);

    fragColor = vec4(tex.rgb,1.0);
}

float count(vec2 p, vec2 off){

    float cnt = 0.;

    cnt += texture(iChannel0, p + vec2(off.x,off.y)).y;
    cnt += texture(iChannel0, p + vec2(off.x,0.0)).y;
    cnt += texture(iChannel0, p + vec2(off.x,-off.y)).y;
    cnt += texture(iChannel0, p + vec2(0.,off.y)).y;
    cnt += texture(iChannel0, p + vec2(0.,-off.y)).y;
    cnt += texture(iChannel0, p + vec2(-off.x,off.y)).y;
    cnt += texture(iChannel0, p + vec2(-off.x,0.0)).y;
    cnt += texture(iChannel0, p + vec2(-off.x,-off.y)).y;

    return cnt;

}

void mainImage( out vec4 data, in vec2 fragCoord )
{

   //fragCoord -= mod(fragCoord, float(S));
    vec2 off = S/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;//  + (off * .5);
    vec2 st = fract(uv*skale);
    vec2 id = floor(uv*skale);
    vec3 mouse = iMouse.xyz;

    float c;

    float state,val,speed,resx;


    //set up / spacebar reset
    if(iFrame == 0 || texture(iChannel1,  vec2(0.126953125, .25)).x > 0.){

        uv = fragCoord/iResolution.xy;

        //st = fract(uv*skale);        
        id = floor(uv*skale);


        //val = rnd(id);
        state = step(0.65,rnd(uv));
        val = 0.;
        //speed = rnd(id+vec2(1.))*0.002+0.99;
        //resx = iResolution.x;

    }
    else{
        //get own texture
        vec4 tex = texture(iChannel0, uv);

        float c = count(uv,off);

        state = tex.y;

            if((c == 2. && state == 1.) || c == 3.){

            //this variation is like a tiling
            //if((c == 2. && state == 1.) || c == 1.){

                state = 1.;
            }

            //if cell dies, increment val, the death counter
            //"Death Counting"
            else{


                state = step(0.998,rnd(uv));//0.;
                val += 1.;
            }    

    }


    data = vec4(val, state, speed, rnd(vec2(uv.x*uv.y)));
}

//scaling and aspect ration have to happen for st and not the orig uv
#define S 1.
#define skale vec2(S,S)
//*(iResolution.y/iResolution.x))
//#define off vec2(iResolution.x/skale.x,iResolution.y/skale.y)

float rnd(vec2 id){
    return fract(sin(id.x*12.99+id.y*800.+40.)*51343.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates 0 (from to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //for color

    //the 256 effects the highlights. lower for more highlights.
    vec2 st = uv;// fract(uv*iResolution.xy/256.);

    vec4 tex = texture(iChannel0,uv/8.);

    vec3 col;// = vec3(step(0.5,tex.y));// * (1.0-length(st-vec2(0.5))-0.3);

       //glow 
    col = 0.5+0.5*cos(vec3(tex.y*tex.x*tex.y*1.)+vec3(0,2,4)/4.+tex.y*tex.x*3. +3.);



    col = pow(col,vec3(2.))*5.;
    col = mix(col, texture(iChannel1,uv).rgb, .9);
    // Output to screen
    fragColor = vec4(col,1.0);
}
