void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 dist = ( 0.5 - abs( vec2( 0.5 ) - uv ) ) * 0.25;
    //float alpha = max( dist.x, max( dist.y, 0.0 ) );
    float alpha = sqrt( dist.x*dist.x + dist.y*dist.y );
    vec3 col = vec3(alpha);

    // Output to screen
    fragColor = vec4(col,1.0);
}
