float Band(float t, float start, float end, float blur) {
    float step1 = smoothstep(start-blur, start+blur, t);
    float step2 = smoothstep(end+blur, end- blur, t);

    return step1*step2;
}


float Rect(vec2 uv, vec2 pos, float width, float height, float blur) {
    uv -= pos;

    float band1 = Band(uv.x, -width/2., width/2., blur);
    float band2 = Band(uv.y, -height/2., height/2., blur);


    float numberOfSegments = 7.;

    float segmentHeight = height/numberOfSegments;

    float gapCenter = -height/2.;
    for(float i = 0.; i<numberOfSegments-1.; i += 1.) {
        gapCenter+= segmentHeight;
        band2 -= Band(uv.y, gapCenter-height/20., gapCenter+height/20., blur);
    }

    //uv.(x+blur) uv.(x+segmentHeight+blur)
    return band1*band2;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;

    float mask = Rect(uv, vec2(0., 0.), 0.1, 0.6, 0.01);
    vec3 col = mask*vec3(1., 1., 1.); //set color 

    fragColor = vec4(col, 1.0);
}
