void mainImage( out vec4 O, vec2 U )
{
    U /= iResolution.xy;

    O = vec4(0); 

    for( float i = 1.; i < 15.; i++ )
        if (U.y > .5 ) O +=   .01 /  sin( U.x*i - (i-i/2.) );
     // if (U.y > .5 ) O +=   .01 /  sin( U.x*i + (i-i/2.) ); // indeed, does this !
        else           O +=   .01 /  sin( U.x*i - ( i/2. ) );

        // but ok for 2*i-i vs i , for shorter loop, 
        // and for simpler expr. replacing sin by abs shows bug too.
}
