void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const float warp = 0.02;
    const float warp_speed = 0.01;
    const float warp_scroll_speed = 0.05;
    const float water_scroll_speed = 0.005;
    const float water_blueness = 0.5;
    const float water_warp = 0.04;

    // find (pixelated) screen position in clip space
    vec2 pixels = floor(iResolution.xy / 4.0);
    vec2 scrn_pos = fragCoord.xy / iResolution.xy;

    scrn_pos = floor(scrn_pos * pixels) / pixels;

    // find noise value(s)
    vec2 noise_pos1 = scrn_pos + vec2(iTime * warp_scroll_speed);
    vec2 noise_pos2 = scrn_pos + vec2(iTime * -warp_scroll_speed);

    noise_pos1.y *= 2.0;

    vec2 noise = texture(iChannel0, vec3(noise_pos1, iTime * warp_speed)).rg
               + texture(iChannel0, vec3(noise_pos2, iTime * warp_speed)).rb;

    noise * 0.5;

    // background texture color, using the noise value to warp
    fragColor = texture(iChannel1, (scrn_pos + vec2(noise * warp)) * 0.5);

    // water coloring
    const vec4 foam_color = vec4(204.0, 218.0, 224.0, 255.0) / 255.0;
    const vec4 water_color = vec4(31.0, 83.0, 150.0, 255.0) / 255.0;

    vec2 water_pos = vec2(iTime * water_scroll_speed, 0.0);
    vec2 water_noise = texture(iChannel0, vec3(scrn_pos, 0.0)).gb;
    float water_sample = texture(iChannel0, vec3(scrn_pos * 0.25 + water_noise * water_warp + water_pos, 0.0)).b;

    vec4 water = mix(water_color, foam_color, pow(1.0 - abs((water_sample * 2.0) - 1.0), 1.8));

    fragColor = mix(fragColor, water, water_blueness);
}
