// Antialiased variant of "Pixel perfect outline" by portponky. https://shadertoy.com/view/7tj3Wh

#define inside(P) step(.1, length(  texture(iChannel0, (P)/iResolution.xy)  \
                                    - vec4(.05, .64, .15, 1) ) )

void mainImage( out vec4 O, vec2 U )
{
    O = vec4(0);

    for (int k=0; k<9; k++)
        O += inside( U + vec2(k%3,k/3)-1. );

    O = min( 9.-O , O ) / 5.;

// --- with original colors
//  O = O.a>4. ? mix( vec4(0),  vec4(1), (9.-O)/5. )
//             : mix( vec4(0, .58, .88, 1),  vec4(1), O/5. );   
}
