vec4 matcapSphere(vec2 uv)
{
    uv = (uv - 0.5) * 2.0;
    vec3 n = vec3(uv, sqrt(1.0 - clamp(dot(uv, uv), 0.0, 1.0)));
    // convert to color range
    vec3 color = 0.5 + 0.5 * n;
    return mix(vec4(0.5, 0.5, 1.0, 0.0), vec4(color, 1.0), smoothstep(1.01, 1.0, dot(uv, uv)));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    fragColor = matcapSphere(uv);
}
