void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float val1 = abs(sin((fragCoord.y / iResolution.y + (iTime / (16.0))) * 15.0 + rand(uv)));
    float val2 = abs(sin((fragCoord.y / iResolution.y + (iTime / 8.0)) * 20.0 + rand(uv)));
    float val3 = abs(sin((fragCoord.x / iResolution.x + (iTime / 16.0 + rand(uv))) * 3.0));
    float val4 = abs(sin((fragCoord.x / iResolution.x + (iTime / 8.0 + rand(uv))) * 5.0));

    float val = (val1 + val2 + val3 + val4) / 4.0;

    vec2 uvr = fragCoord / iResolution.xy;

    vec4 scol = texture(iChannel0, uvr);

    float shadow = sin(fragCoord.x / iResolution.x * 3.14) + sin(fragCoord.y / iResolution.y * 3.14);
    shadow /= 2.;
    float scan = (shadow + val) / 2. + 0.1f;

    vec3 col = vec3(scol.x * scan, scol.y * scan, scol.z * scan);

    fragColor = vec4(col, 1.0);
}

