// Trying to mimick infinite zoom of https://www.shadertoy.com/view/fl2SDV
// with the procedural base of "shortest maze 5 (65 chars)" https://shadertoy.com/view/lt2cRR

void mainImage(out vec4 O, vec2 u) {

    float s = .33 + .67* exp2( mod(iTime,2.) ), k = 1., z = 0.;
    vec2  R = iResolution.xy,
          U = ( u+u - R ) / R.y / s, I,V;

    for ( O-=O; z < 5.; U*=3., k*=3., z++ )         // fractal levels
       I = floor(abs(U/1.5)+.5),
       max(I.x,I.y) == 1.?                          // draw maze tile
            V = ( U+z+floor(iTime/2.) ) *16. + 4.,  // offset level seed
            O += mod( V [ int( 1e4*length(ceil(V/8.)) ) % 2 ] , 8. ) *s/k *R.y/72. :O;
}               // heart of base golfed maze                         // 1 pixel width
