float vignette(float x, float intensity) {
    intensity = 1.-intensity;
    return (4.-4.*intensity)*(x-x*x)+intensity;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    float time = sin(iTime)/2.0+0.5;
    vec3 col = vec3(vignette(uv.x, time)*vignette(uv.y, time));
    fragColor = vec4(pow(col, vec3(.4545)),1.0);
}
