void mainImage(out vec4 O, vec2 U) {
vec2 uv = U/iResolution.xy;
   uv = floor(uv * 5.)/5.;
   O = texture(iChannel0, uv);
}

vec4 blur(sampler2D sp, vec2 U, float scale) {
    vec4 O = vec4(0);  
    int s=  100;

    for ( int i = 0; i < s; i++ ) {
        float d = float(i)/float(s) - .5;
        O += texture( sp, U + vec2(0., scale * d)  );
    }

    return O / O.a;
}

void mainImage(out vec4 O, vec2 U) {
    O = blur( iChannel0, U/iResolution.xy, 0.125 );
}

