void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv = (uv-0.5)*2.0;

    vec3 col = vec3(0.0,0.5,1.0);

    //camera
    vec3 eye = vec3(0,0.3,-3);
    vec3 up = vec3(0,1,0);
    vec3 w = vec3(sin(iTime), sin(iTime)/5., cos(iTime));
    vec3 u = -normalize(cross(up,w));
    vec3 v = -normalize(cross(w,u));

    vec3 ray = eye -3.0*w + uv.x*u + uv.y*v;

    float an = dot(ray, -up);
    float an2 = dot(ray, vec3(1,0,0));
    if( an >= 0.) {
        col = vec3(0,1.0,0);
        col.rgb = vec3(an*abs(an2));
    }


    fragColor = vec4(col,1.0);
}
