vec2 lerp(vec2 a, vec2 b, float t) {
    float c = sin(t)*0.5+0.5;
    return a*c + b*(1.0-c);
}

vec4 lerp4(vec4 a, vec4 b, float c) {
    return a*c + b*(1.0-c);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec4 tileColor1 = vec4(0.0, 0.0, 0.0, 1.0);
    vec4 tileColor2 = vec4(1.0, 1.0, 1.0, 1.0);

    float tileSize = 70.0;
    float xSway = 10.0, ySway = 5.0;
    float xSpeed = 1.0, ySpeed = 0.5;
    vec2 coord = fragCoord + vec2(sin(iTime*xSpeed)*xSway, cos(iTime*ySpeed)*ySway);

    int tileId = int(floor(coord.x / tileSize)) + int(floor(coord.y / tileSize));
    if (tileId % 2 == 0) {
        fragColor = tileColor1;
    } else {
        fragColor = tileColor2;
    }

    vec2 seeds[5] = vec2[5](
        lerp(vec2(50, 50), vec2(400, 70), iTime*0.4),
        lerp(vec2(70, 150), vec2(300, 100), iTime*1.2),
        lerp(vec2(200, 20), vec2(200, 410), iTime*0.7),
        lerp(vec2(750, 400), vec2(400, 200), iTime*0.05), 
        lerp(vec2(750, 400), vec2(700, 20), iTime*1.9)
    );

    vec4 colors[5] = vec4[5](
        vec4(1.0, 0.0, 0.0, 1.0),
        vec4(0.0, 1.0, 0.0, 1.0),
        vec4(0.0, 0.0, 1.0, 1.0),
        vec4(0.6, 0.4, 0.0, 1.0),
        vec4(0.7, 0.0, 0.7, 1.0)
    );

    for (int i = 0; i < 5; i++) {
        vec2 seed = seeds[i];
        vec4 color = colors[i];
        vec2 mouseDiff = fragCoord - seed;
        float mouseDistCenter = sqrt(mouseDiff.x*mouseDiff.x + mouseDiff.y*mouseDiff.y);
        float factor = (50.0 - mouseDistCenter) / 50.0;
        factor = max(factor, 0.0);
        fragColor = lerp4(color, fragColor, factor);
    }
}
