void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv.x -= .5;
    uv.x *= iResolution.x/iResolution.y;

    // remove the trig funcs to get normal checkerboard
    float k = sin(iTime)*10.;
    float a = cos(iTime)*10.;
    uv += vec2(cos(iTime), sin(iTime));
    vec3 col = mix(vec3(0,0,0), vec3(1,1,1), step(1./a,mod(uv.x+ceil(uv.y*k)/k, 2./a)));

    // Output to screen
    fragColor = vec4(col,1.0);
}
